void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pxl = fragCoord.xy/iResolution.xy;
    vec3 red    = vec3(0.929, 0.125, 0.141);
    vec3 green  = vec3(0.153, 0.557, 0.263);
    vec3 yellow = vec3(0.996, 0.741, 0.067);
    vec3 white  = vec3(1.0, 1.0, 1.0);

    vec2 centre = vec2(0.5, 0.5);

    vec2 qxl = pxl - centre;

    float r = 0.2 + 0.05 * cos(atan(qxl.y, qxl.x) * 21.0 + iTime * 15.0);
    float star = smoothstep(r, r+0.001, length(qxl));

    vec3 colour;

    // Branchless version of https://www.shadertoy.com/view/mls3D2
    int h = 1 << int(pxl.y * 3.0);
    colour = red * float((h & 4)>>2) + white * float((h & 2)>>1) + green * float((h & 1));
    colour = (1.0 - star)*yellow + star*colour;

    fragColor = vec4(colour, 1.0);

}
