//con man christmas
//chatGPT draw an xmas tree in glsl. Sure!

#define PI 3.1415926535897932384626433832795

// The height of the tree
const float treeHeight = 0.8;

// The width of the tree at the base
const float treeWidth = 0.4;

// The color of the tree
const vec3 treeColor = vec3(0.12, 0.54, 0.11);

// The color of the tree's lights
const vec3 lightColor = vec3(1.0, 0.8, 0.6);

// The number of lights on the tree
const int numLights = 5;

// The radius of each light
const float lightRadius = 0.05;

// The position of each light
const vec2 lightPositions[numLights] = vec2[numLights](
  vec2(-0.2, 0.3),
  vec2(0.2, 0.3),
  vec2(-0.15, 0.5),
  vec2(0.15, 0.5),
  vec2(0.0, 0.7)
);

// The color of the tinsel
const vec3 tinselColor = vec3(0.9, 0.9, 0.9);

// The width of the tinsel strands
const float tinselWidth = 0.01;

// The length of the tinsel strands
const float tinselLength = 0.1;

// The number of tinsel strands
const int numTinselStrands = 15;

// The position of each tinsel strand
const vec2 tinselPositions[numTinselStrands] = vec2[numTinselStrands](
  vec2(-0.3, 0.3),
  vec2(-0.25, 0.35),
  vec2(-0.2, 0.4),
  vec2(-0.15, 0.45),
  vec2(-0.1, 0.5),
  vec2(-0.05, 0.55),
  vec2(0.0, 0.6),
  vec2(0.05, 0.65),
  vec2(0.1, 0.7),
  vec2(0.15, 0.75),
  vec2(0.2, 0.8),
  vec2(0.25, 0.85),
  vec2(0.3, 0.9),
  vec2(0.25, 0.95),
  vec2(0.2, 1.0)
);

void mainImage(out vec4 FragColor, vec2 FragCoord) {
  // Animation term added for effect by eiffie
  float a = abs(sin(iTime*.01));

  // Calculate the position of the current fragment
  vec2 fragPos = (1. + a) * FragCoord.xy / iResolution.xy - vec2(a * .5);

  // Calculate the distance of the fragment from the base of the tree
  float dist = length(fragPos - vec2(0.5, 0.0));

  // Calculate the width of the tree at the current fragment's height
  float treeWidthAtHeight = treeWidth * (1.0 - fragPos.y / treeHeight);

  // If the fragment is within the bounds of the tree, color it the tree's color
  if (dist < treeWidthAtHeight / 2.0) {
    FragColor = vec4(treeColor, 1.0);
  } else {
    // Otherwise, check if the fragment is within the bounds of any of the lights
    for (int i = 0; i < numLights; i++) {
      vec2 lightPos = lightPositions[i];
      float lightDist = length(fragPos - lightPos);
      if (lightDist < lightRadius) {
        FragColor = vec4(lightColor, 1.0);
        break;
      }
    }
  }
  // Check if the fragment is within the bounds of any of the tinsel strands
  for (int i = 0; i < numTinselStrands; i++) {
    vec2 tinselPos = tinselPositions[i];
    vec2 tinselDir = normalize(vec2(0.0, 1.0));
    float tinselDist = dot(fragPos - tinselPos, tinselDir);
    if (tinselDist >= 0.0 && tinselDist < tinselLength && abs(fragPos.x - tinselPos.x) < tinselWidth / 2.0) {
      FragColor = vec4(tinselColor, 1.0);
      break;
    }
  }

}
