//Macros from here https://www.shadertoy.com/view/llySRh
#define mouseUp      ( iMouse.z < 0. )                  // mouse up even:   mouse button released (well, not just that frame)
#define mouseDown    ( iMouse.z > 0. )                    // mouse down even: mouse button is down
#define mouseClicked ( iMouse.w < 0. )                  // mouse clicked:   mouse button currently clicked

const bool PERSISTENT_MOUSE = false; //If you click in the rectangle, it'll stay green even if you drag it out.

bool insideBox(vec2 bottomCorner, vec2 boxSize, vec2 position){
    vec2 relativeDistanceFromCorner = position - bottomCorner;

    return ((relativeDistanceFromCorner.x >= 0.0f) && (relativeDistanceFromCorner.x <= boxSize.x)) && ((relativeDistanceFromCorner.y >= 0.0f) && (relativeDistanceFromCorner.y <= boxSize.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 boxSize = vec2(50.0f, 100.0f);
    vec2 boxPosition = vec2(100.0f, 50.0f);

    float brightness = insideBox(boxPosition, boxSize, fragCoord)? 1.0f : 0.0f;

    vec3 col;

    if(mouseDown){
        vec2 mouse = PERSISTENT_MOUSE? iMouse.zw : iMouse.xy;
        if(insideBox(boxPosition, boxSize, abs(mouse))){
            col = vec3(0.0f, brightness, 0.0f);
        }else{
            col = vec3(brightness, 0.0f, 0.0f);
        }
    }else{
        col = vec3(brightness, brightness, brightness);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
