#define pi 3.14159265
#define test 0
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x/iResolution.y;
    vec2 texSize = vec2(textureSize(iChannel0,0));
    vec2 offset = vec2((aspect-1.0)/2.0,0.0);
    vec2 uv = (fragCoord)/iResolution.y - offset;
    //get the coords relative to the texture
    vec2 texCoord = floor(uv*texSize);


    float mosaicSize = 30.0;//floor(sin(mod(iTime/10.0*pi - pi/2.0,0.3))*0.2 * texSize.x);
	vec2 mosaicBlockSize = vec2(mosaicSize,mosaicSize);
    //use modulo to get the top left pixel position of the current group
    vec2 blockStart = fragCoord; //texCoord;
    blockStart.x -= mod(blockStart.x,mosaicBlockSize.x);
    blockStart.y -= mod(blockStart.y,mosaicBlockSize.y);
  	vec4 col = texture(iChannel0,blockStart/texSize);
    // Output to screen
    fragColor = vec4(col);

    vec2 uv2 = fragCoord / iResolution.xy;
    vec4 texColor = texture(iChannel0, uv2);

    if(uv.x > 1.0 || uv.x < 0.0) fragColor = texColor; //vec4(0.0);

}
