void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 o_fc_nor = fragCoord.xy / iResolution.xy;
    vec4 o_img = texture(iChannel0, o_fc_nor);
    fragColor = vec4(o_img.r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_fc1_nor = vec2(1.) / iResolution.xy;
    vec2 o_fc_nor = o_fc1_nor * fragCoord.xy;

    vec4 o_col_last_xp1 = texture(
        iChannel0, 
        vec2(o_fc_nor.x + o_fc1_nor.x , o_fc_nor.y)
    );
    if(o_fc_nor.x >= 1.-(o_fc1_nor.x*1.)){
        float n_timedomain_amp = texture(iChannel1, vec2(o_fc_nor.y, 1.))[0];
        float n_freqdomain_amp = texture(iChannel1, vec2(o_fc_nor.y, 0.))[0];
        fragColor = vec4(
            n_freqdomain_amp
        );
    }else{
        fragColor =  o_col_last_xp1;   
    }

}
