void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 o_fc = (fragCoord.xy-iResolution.xy*0.5)/iResolution.y;
    vec2 o_fc_2 = o_fc;// this makes a copy of o_fc and stores it into o_fc_2
    o_fc_2 *= sin(iTime);
    fragColor = vec4(length(o_fc));
}
