float f_n_dpat1(vec2 o, float n){
    o = abs(o);
    float nd = max(o.x*n, o.y);

    if(o.x > abs(o.y-n)){
        return o.x;
    }
    return o.y;

    return nd;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 o_fc = (fragCoord.xy-iResolution.xy*0.5)/iResolution.y;
    vec2 o_fcorig = o_fc;
    o_fc *= 20.;
    vec2  o_fc_fract = fract(o_fc)-.5;

    float n_t = iTime*2.;


    float n_id = atan(floor(o_fc.x), floor(o_fc.y));
    n_t+=n_id;
    float n = f_n_dpat1(o_fc_fract, sin(n_t)*0.5+0.5)*2.;
    n = (sin(n_t+n+n_id*33.)*.5+.7)*n;
    fragColor = vec4(n);
    fragColor = sqrt(fragColor);
    fragColor.rb *= floor(o_fc)/5.;
    fragColor.gb *= fract(length(fract(o_fc)-.5)+n_t*.2);
}
