void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel1, uv);
}

/*The Update Shader
 in here we read the weights in the network from
 iChannel1 on color r, and output the next weight and output

 the weight should be output on color r
 the neuron output, its current activation level, on color g
*/

#define RADIUS 10.0

float sigmoid(float x) {
    return 1.0 / (1.0 + exp(-x));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	vec2 nppd = 1.0/iResolution.xy * 1.0;

    vec2 nmp = iMouse.xy / iResolution.xy;

    vec3 c = vec3(0.0);

    // network values and local neighborhood
    // where mc is the center pixel, the current neuron
    vec3 tl = texture(iChannel1, abs(uv + nppd * vec2( -1.0, -1.0))).rgb;
    vec3 tc = texture(iChannel1, abs(uv + nppd * vec2(  0.0, -1.0))).rgb;
    vec3 tr = texture(iChannel1, abs(uv + nppd * vec2(  1.0, -1.0))).rgb;
    vec3 ml = texture(iChannel1, abs(uv + nppd * vec2( -1.0,  0.0))).rgb;
    vec3 mc = texture(iChannel1, abs(uv + nppd * vec2(  0.0,  0.0))).rgb;
    vec3 mr = texture(iChannel1, abs(uv + nppd * vec2(  1.0,  0.0))).rgb;
    vec3 bl = texture(iChannel1, abs(uv + nppd * vec2( -1.0,  1.0))).rgb;
    vec3 bc = texture(iChannel1, abs(uv + nppd * vec2(	0.0,  1.0))).rgb;
    vec3 br = texture(iChannel1, abs(uv + nppd * vec2(  1.0,  1.0))).rgb;

    // before frame 20, set your pixel weights to be random by just using the input
    // from iChannel0 which is a noisy texture.
    if (iFrame < 20)
    {
        tl = texture(iChannel0, uv + nppd * vec2( -1.0, -1.0)).rgb;
        tc = texture(iChannel0, uv + nppd * vec2(  0.0, -1.0)).rgb;
        tr = texture(iChannel0, uv + nppd * vec2(  1.0, -1.0)).rgb;
        ml = texture(iChannel0, uv + nppd * vec2( -1.0,  0.0)).rgb;
        mc = texture(iChannel0, uv + nppd * vec2(  0.0,  0.0)).rgb;
        mr = texture(iChannel0, uv + nppd * vec2(  1.0,  0.0)).rgb;
        bl = texture(iChannel0, uv + nppd * vec2( -1.0,  1.0)).rgb;
        bc = texture(iChannel0, uv + nppd * vec2(	0.0,  1.0)).rgb;
        br = texture(iChannel0, uv + nppd * vec2(  1.0,  1.0)).rgb;
    }

    /* this is where you would compute the output of the current 
    neuron (mc) and its new weight
    input is the output of the "in_circle" function which 
    takes in the pixel coord of mc and outputs true or false. 
    the neuron then uses that as input, to compute its next output, 

    and updates itself based on its neighbors last outputs
    */
    // in circle
    float in_circle = step(length(uv - vec2(0.5, 0.5)), RADIUS);
    float error = in_circle - mc.g;
    float next_weight = mc.r + 0.01 * error * (tl.g + tc.g + tr.g + ml.g + mr.g + bl.g + bc.g + br.g);
    float out_put = sigmoid(next_weight * in_circle);

    fragColor = vec4( next_weight, out_put, 0.0, 1.0 );

    // this lets us draw weights, its weird but should help with debugging
    if(iMouse.z > 0.0)
    	fragColor = max(fragColor,vec4(step(length(iMouse.xy - fragCoord),RADIUS)));
        }
