vec3 rgb(float r, float g, float b)
{
    return vec3(r/255.0, g/255.0, b/255.0);
}
mat2 rotAngle(float a)
{
    return mat2(cos(a),-sin(a),sin(a),cos(a));
}

float HexDist(vec2 p) {
    p = abs(p);

    float c = dot(p, normalize(vec2(1,1.73)));
    c = max(c, p.x);

    return c;
}

vec2 HexCoords(vec2 uv) {
    vec2 r = vec2(1, 1.73);
    vec2 h = r*.5;

    vec2 a = mod(uv, r)-h;
    vec2 b = mod(uv-h, r)-h;

    vec2 gv = dot(a, a) < dot(b,b) ? a : b;

    return gv;
}

float rotStar(vec2 uv, float scale, float rotspeed)
{
    vec2 st = vec2(atan(uv.y,uv.x)/6.28318 +0.5,length(uv));
    vec2 fw = fwidth(uv);

    float rot = smoothstep(0.0,1.0,fract(iTime*rotspeed));
    rot+=0.05;
    float curve1 = (max(fract((st.x+rot)*5.0),1.0-fract((st.x+rot)*5.0))-0.5)*2.0;
    float curve2 = sin(3.14159*((st.x+rot)+0.25)*2.0*5.0)*0.2+0.5;
    //float starx = sin((st.x+rot+0.1)*3.1415926*2.0*5.0)*0.2+1.0;
    curve1+=1.0;
    curve2+=1.0;
    float starx = mix(curve1,curve2,sin(iTime)*0.5+0.5);
    starx *= scale;
    float stary = st.y;
    return smoothstep(starx+length(fw),starx,stary);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float fw = fwidth(uv.x);
    vec3 col = vec3(0);

    vec2 hexuv = uv;
    hexuv*=rotAngle(radians(45.0));
    hexuv*=15.0;
    hexuv+=100.0;
    hexuv.y-=iTime;
    vec2 hc = HexCoords(hexuv);

    vec3 ringbgcolor = rgb(215.0,123.0,186.0);
    vec3 ringcolor1 = rgb(238.0,195.0,154.0);
    vec3 ringcolor2 = rgb(153.0,229.0,80.0);
    col = ringbgcolor;
    col = mix(col,ringcolor1,smoothstep(0.51,0.5,length(hc.xy)));
    col = mix(col,ringcolor2,smoothstep(0.51,0.5,length(hc.xy))*mod(floor(hexuv.x-hc.x),2.0));



    vec2 recbaseuv = uv;
    recbaseuv*=15.0;
    recbaseuv*=rotAngle(radians(45.0));

    vec2 recmaskuv = recbaseuv;
    recmaskuv.x +=iTime;
    vec2 recuv = recbaseuv;
    vec2 recgv = fract(recuv);
    recuv+=10.0;
    recmaskuv+=10.0;
    vec2 recid = floor(recuv);

    vec3 bgcolor1= rgb(99.0,155.0,255.0);
    vec3 bgcolor2= rgb(48.0,96.0,130.0);
    vec3 reccolor= mix(bgcolor1,bgcolor2,mod(recid.x,2.0));

    float recmask= mod(floor(recmaskuv).x,2.0)*mod(floor(recmaskuv).y,2.0);

    col = mix(col,reccolor,mod(floor(recmaskuv).x,2.0)*recmask);

    vec2 star1pos = vec2(0.1,0.3);
    vec2 star2pos = vec2(-0.3,-0.4);
    vec3 star1color = rgb(138.0,43.0,226.0);
    vec3 star2color = rgb(199.0,97.0,20.0);
    col = mix(col, star1color, rotStar(uv-star1pos,0.05,0.75));
    col = mix(col, star2color, rotStar(uv-star2pos,0.05,0.75));
    fragColor = vec4(col,1.0);
}
