#define R iResolution.xy
#define T (iTime)

/*
*  |=========================================|
*  | GIRL PARAMETERS                         |
*  |=========================================|
*/
///////////////////////////////////////// ||||||||||||||||||||||||||||||||||

#define GIRL_TIME (iTime)              // | WHAT IS THE TIME FOR THE GIRL  |
#define GIRL_SCALE 4.0                 // | HOW BIG IS THE GIRL            |
#define GIRL_MOVE_SCALAR 3.14          // | HOW FAR DOES THE GIRL MOVE     |
#define GIRL_ROTATION_SCALAR 60.0      // | HOW FAST DOES THE GIRL ROTATE  |

///////////////////////////////////////// ||||||||||||||||||||||||||||||||||
/*
*  |=========================================|
*  | GREEN PARAMETERS                        |
*  |=========================================|
*/
#define GREEN_THRESHOLD 8.0
#define GREEN_SCALAR 2560.0

mat2 rot(float angle) { float s=sin(angle);float c =cos(angle);return mat2(c,s,-s,c);}

void mainImage( out vec4 O, in vec2 fc )
{
    vec2 uv = fc/R.xy;
    vec3 bg = texture(iChannel1, uv).rgb;
    vec3 G = vec3(0.02, 0.9, 0.01);
    vec2 girlPosition = vec2(cos(GIRL_TIME), sin(GIRL_TIME)) * GIRL_MOVE_SCALAR;
    vec2 fguv = (vec2(-1.5) + girlPosition*0.5)+(uv*GIRL_SCALE);
    fguv -= 0.5;
    fguv *= rot(radians(GIRL_TIME*GIRL_ROTATION_SCALAR));
    fguv += 0.5;
    vec3 fg = (fguv.x < 0.0 || fguv.x > 1.0 || fguv.y < 0.0 || fguv.y > 1.0) ? G : texture(iChannel0, fguv).rgb;
    O = vec4(mix(bg, fg, clamp(1.0 - (pow(max(0.0, 1.0 - distance(fg, G)), GREEN_THRESHOLD)*GREEN_SCALAR), 0.0, 1.0)), 1.0);
}
