const float PI=3.14159265358979323846;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Centralized coords
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    float d = length(uv);

    //Shrinking affect -remove for just 1 blob
    d = sin(d*2.+iTime*2.0);

    float turnFreq = 8.0;
    float spikeFreq = 1.0;
    float numSpikes = 3.0;

    float theta0 = atan(uv.y,uv.x); //spatial theta
    float theta = atan(uv.y,uv.x)*numSpikes+ iTime*turnFreq; //temporal theta
    theta = mod(theta-PI,2.0*PI)-PI; //Cyclical Time

    float s_len = 0.0 + 0.3*sin(theta0-iTime*spikeFreq);
    float s_wid = 1.0;
    d -= exp(-pow(theta/s_wid,2.0))*s_len;

    float bound = 0.5+0.05*sin(5.0*iTime);
    float range = 0.008;//0.05; // + 0.05*sin(iTime);

    float col = 1.0-exp(-pow((d-bound)/range,2.0));
    float r = exp(-pow((d-bound)/range,2.0));
    float g = exp(-pow((d-bound)/range,2.0));
    float b = exp(-pow((d-bound)/range,2.0));

    // Output to screen
    fragColor = vec4(r,g,b,1.0);
}
