#define PI 3.14159265359

uniform vec2 u_resolution;
uniform float u_time;

float circle(vec2 mid,float r,vec2 st){
    return smoothstep(r,r+0.01,distance(st,mid));
}

float h(float x){
    return -1.*(pow(x-1.,2.))+.8; 
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 R = iResolution.xy;
    vec2 st = fragCoord/R.y;

    float u_time = iTime;
    float speed = .06;

    float time = 1.-fract(-1.720*(pow(u_time*speed-0.5,2.))+.8);
    time = fract(u_time*.5)*2.;

    vec2 cp = vec2(time,h(time));

    // Step will return 0.0 unless the value is over 0.5,
    // in that case it will return 1.0
    vec2 loc = vec2(0.1,0.2);
    float t = st.y*2.-cp.y;
    float c = 1.-circle(cp,0.1,st);

    vec3 color = mix(vec3(0.355,0.555,0.760),vec3(0.915,0.763,0.607),st.y)*(1.-c);

     color += mix(vec3(0.900,0.237,0.038),vec3(0.855,0.792,0.162),t)*c;


    fragColor = vec4(color,1.0);
}
