// @Xor 317 chars
void mainImage( out vec4 O, vec2 C)
{
    float T=smoothstep(1.,5.,mod(iTime,6.)),V;
    vec2 U=fract(C/iResolution.x+vec2(T,.225-T))-.5,
    F=vec2(U.y*.8,0),
    P=abs(U+F);
    V=max(P.x-.04,P.y-.2);
    P=abs(U*mat2(cos(vec4(0,11,33,0)+sign(U.y)*sin(T*94.25)))-F);
    O=vec4(min(min( max(-V, max(P.x,P.y-.2)), abs(V) ), length(U+vec2(.13,-.16))))*5e2-5.;
}

