const float PI = 3.141592653589793;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    vec2 uvDivided = fract(uv * vec2(20.,0.));

    float duration = iMouse.x / iResolution.x;

    vec2 uvDisplaced1 = uv + uvDivided * duration;
    vec2 uvDisplaced2 = uv + uvDivided * (1. - duration);

    vec4 tex1 = texture(iChannel0,uvDisplaced1);
    vec4 tex2 = texture(iChannel1,uvDisplaced2);

    // Output to screen
    fragColor = vec4(mix(tex1,tex2,duration));
}
