void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float mod = fragCoord.y - (42.0* floor(fragCoord.y/42.0)); 
    fragColor = vec4(1.0,1.0,1.0,1.0);
    if((mod <39.0)&&(mod >12.0)){
        fragColor.r = 0.001*fragCoord.x*((sin(iTime)));
        fragColor.g = fragCoord.y*0.001*(cos(iTime)+sin(iTime));
    }
    if(mod>20.0){
         fragColor.g = 0.0012*fragCoord.x*cos(iTime);
         fragColor.r = 0.0021*fragCoord.y*sin(iTime);
   }
}
