void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float time = iTime;

    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float radius = 0.3;

    float dist = length(uv);

    float insideCircle = smoothstep(radius, radius + 0.01, dist);

    vec3 color = 0.5 + 0.5 * cos(time + vec3(0, 2, 4));

    vec3 circleColor = mix(color, vec3(0.0), insideCircle);

    fragColor = vec4(circleColor, 1.0);
}
