float random (vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233)))* 43758.5453123);
}

vec4 cirkl(vec2 uv, vec2 center, float radius, vec4 color){
    //if (length(uv - center) < radius)
        //return color;
    //else return vec4(0.0, 0.0, 0.0, 0.0);
    float fy = length(uv-center) - radius;
    float smoot = smoothstep(0., radius, fy);
    return color*(1.-smoot); 
}
vec3 palette(in float t)
{
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 0.7, 0.4);
    vec3 d = vec3(0.00, 0.15, 0.20);
    return a + b*cos(6.28318*(c*t+d));					
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord.xy ) /iResolution.y;
    fragColor = vec4(0.);
    for(float i=1.;i < 50.;i++){
        vec4 color = cirkl(uv, 
                                vec2(random(vec2(sin(i)))*2., 
                                    random(vec2(pow(i-random(vec2(i,i)),2.)+exp(i*iTime*pow(0.1,7.)),sin(i*iTime*pow(0.1,7.))))),
                                random(vec2(i,i))*0.025,
                                vec4(palette(i), 1.0));
        if (fragColor.x == 0.)      
            fragColor += color;
    }
}
