void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	int intx = int(fragCoord.x);
	int inty = int(fragCoord.y);
	while (intx > 0 && inty > 0) {
		int xmod = intx % 3;
		int ymod = inty % 3;

		if (xmod == 1 && ymod == 1) {
			fragColor = vec4(0.0, 0.0, 0.0, 1.0);
			return;
		}

		intx /= 3;
		inty /= 3;
	}

	// Output to screen
	fragColor = vec4(1.0, 1.0, 1.0, 1.0);
}
