void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normal Coordinates
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 6.0;

    vec2 m = vec2(iMouse.x/iResolution.x, iMouse.y/iResolution.y);
    m -= 0.5;
    m *= 1.0;
    float r = 0.0;
    float g = 0.0;
    float b = 0.0;
    // Polar
    float i = 0.0;
    float maxi = 15.0;
    float bailout = 0.0;
    while(i < maxi && bailout == 0.0){
        uv = vec2(sqrt(uv.x*uv.x+uv.y*uv.y), atan(uv.y,uv.x));
        uv = vec2(uv.x-1.0, (uv.y*2.0));
        uv = vec2(uv.x*cos(uv.y), uv.x*sin(uv.y));
        r = r + uv.x/maxi/0.5;
        g = g + uv.y/maxi/0.5;
        b = b + ((uv.x+uv.y)/3.0)/maxi/0.5;
        uv += m;
        i++;
    }
    // Output to screen
    float a = 0.4;
    fragColor = vec4(r+a, g+a, 0.0, 0.0);
}
