void mainImage( out vec4 fragColor, in vec2 c )
{   
    float w = iResolution.x;
    float h = iResolution.y;
    float x = c.x;
    float y = c.y;
    float t = myfn(fract(floor(iTime)/1026.)*1071.+fract(iTime));
    float i = x*0.11 + w*y*0.11 + t*6667.;
    float v1 = myfn( i*0.9999997 );
    float v2 = fract( myfn( i*1.1958292 )+0.6247217 );
