float round_box(in vec2 p, in vec2 b, in vec4 r) 
{
    r.xy = (p.x > 0.0) ? r.xy : r.zw;
    r.x  = (p.y > 0.0) ? r.x  : r.y;
    vec2 q = abs(p) - b + r.x;
    return min(max(q.x, q.y), 0.0) + length(max(q, 0.0)) - r.x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

	float d = round_box(p, vec2(1.0, 0.9), vec4(0.1, 0.1, 0.1, 0.1));

    vec3 col = !(d < 0.0 && d > -0.1) ? vec3(0.9,0.6,0.3) : vec3(0.65,0.85,1.0);
    if (d > 0.0) {
        col *= 1.0 - exp(-16.0*abs(d));
    }
    if (d < -0.1) {
        col *= 1.0 - exp(-16.0*abs(d + 0.1));
    }
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)) );
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d + 0.1)) );

	fragColor = vec4(col, 1.0);
}
