float remap (float t, float fX, float fY, float tX, float tY)
{
    return ((t-fX)/(fY-fX)) * (tY-tX) + tX; 
}

float circle(vec2 uv, float r)
{
    float d = length(uv);

    float e = sin(uv.x * 20.) * 0.5;
    float f = sin(uv.y * 20.) * 0.5;

    return smoothstep(r, r-0.001, d);
}

vec3 love(vec2 uv, float radious, float speed)
{
    uv.x = abs(uv.x);

    float t = 0.6 * sin(iTime * speed);
    t = remap(t, -0.6, 0.6, 0., -0.6); 

    uv.x += uv.y * t;
    float c = circle(uv, radious);

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    return vec3(c) * col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 love = love(uv, 0.3, 4.);
    fragColor = vec4(love,1.0);
}
