void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord;

    uv -= vec2 (0.1,0.1)*iResolution.xy;

    vec3 col = vec3(0.0,0.0,0.0);

    float r = 350.0;
    if(uv.x * uv.x + uv.y * uv.y < r * r){
        float z = sqrt(r * r - uv.x * uv.x - uv.y * uv.y);
        vec3 normal =  normalize(vec3(uv.x, uv.y, z));
        vec3 light = normalize(vec3(0.1, 0.1, 0.1));
        float diffuse = clamp(dot(normal, light), 0.3, 0.3);
        float I = diffuse * 0.7 + 0.3;
        col = vec3 (I,0.6,1.0);
    }

    // Output to screen
    fragColor = vec4(col,2.0);
}
