void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Compute UV coordinates, from 0 to 1
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Green
    vec3 greenScreenColor = vec3(0, 1, 0);

    // iChannel0 stores a default ShaderToy video of Britney Spears. We selected this ourselves from Shadertoy's bank of videos. 
    // This line is retrieving texels from the Britney recording, which are the color values for each coordinate (at any given point).  
    vec4 inputColor = texture(iChannel0, uv);

    // This value affects how much of a green halo we see. If it's too high, we won't see the green halo, but Britney will get holes in her body.
    float threshold = 0.7;

    // Check if the current pixel color is similar to the green screen color. If so, we replace it with the background.
    if (distance(inputColor.rgb, greenScreenColor) < threshold)
    {
        vec4 background = texture(iChannel1, uv);
        fragColor = background;
    } 
    else 
    {
        // Keep the original color.
        fragColor = inputColor;
    }

}








