vec3 palette( in float t ) 
{

    vec3 a = vec3(0.500, 0.748, 0.848);
    vec3 b = vec3(0.5,0.5,0.5);
    vec3 c = vec3(1.0,1.0,1.0);
    vec3 d = vec3(0.300, 0.293, 0.397);
    return a + b*cos( 6.28318*(c*t+d) );
}

const float PI = 3.1459;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 unv = fragCoord/iResolution.xy;
         unv.x -= (iResolution.x-iResolution.y)/iResolution.x/2.0;


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = unv;
         uv.x *= (iResolution.x/iResolution.y);
    vec2 unv0 = unv;
         unv0.x *= (iResolution.x/iResolution.y);

    vec3 finalcolor = vec3(0.0);

    float i1=2.0;

    for(float i = 0.0; i<i1; i++) {

             uv = (uv-vec2(0.5,0.5))*1.0;

        vec2 uv0 = unv0-vec2(0.5,0.5);

        uv = fract(uv*2.0) - 0.5;

        float d = (abs(uv.x)+abs(uv.y));//abs(uv.x)+abs(uv.y);
              d = sin(d*8.+iTime*0.8)/8.;
              d = 0.02/abs(d);
        vec3 col = palette((abs(uv0.x)+abs(uv0.y))+iTime*0.5);
             col = col*pow(d,1.2);
            finalcolor += col; 
    }

    finalcolor = finalcolor/i1;

    fragColor = vec4(finalcolor,1.0);
}
