float Hash21(vec2 p)
{
    p = fract(p*vec2(1234.56, 789.987));
    p += dot(p, p +34.23+fract(iTime/5000.));
    return fract(p.x*p.y);
}

//https://iquilezles.org/articles/palettes/
vec3 palette( float t ) {
    vec3 a = vec3(0.8, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 0.8, .8);
    vec3 d = vec3(0.263+fract(iTime/5.),0.416,0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -.5*iResolution.xy)/iResolution.y;
    uv.x = abs(uv.x);

    vec2 uv0 = uv;
    //Settings
    float gridSize = 20.;
    float lineWidth = .3;

    vec3 col = vec3(0);
    uv *=gridSize;//*iTime/10.;
    vec2 gv = fract(uv)-0.5;//+fract(iTime/10.);
    vec2 id = floor(uv);

    //random number between 0 and 1
    float n = Hash21(id);
    if(n<.5) gv.x *= -1.;

    float d = abs(abs(gv.x + gv.y)-.5);
    float mask = smoothstep(-0.01,.01,d - lineWidth);

    col+=mask;
    col*=palette(length(uv0/5.));

    vec2 gv2 = fract(uv0*10.)-0.5;
    vec2 id2 = floor(uv0*10.);
    float check = mod(id2.x+id2.y,2.);


    if(check<.5) gv2.x *= -1.;
    d = abs(abs(gv2.x + gv2.y)-.5);
    mask = smoothstep(-0.01,.01,d - 0.15);

    //col*=mask;//palette(mask*(id2.x+id2.y));

    d = length(gv2);

    vec3 col2 = palette(d);
    d = sin(d*6. + iTime)/8.;
    d = abs(d);

    //col *= col2;
    //col += d;

    fragColor = vec4(col, 1.0);

}
