vec3 Box(vec2 uv, vec2 pos , float size) {
    uv -= pos;
    vec2 bl = step(vec2(size / 2.), uv);
    vec2 tr = step(vec2(size / 2.), 1.0 - uv);

    return vec3(bl.x * bl.y * tr.x * tr.y);
}

mat2 scale(vec2 _scale){
    return mat2(_scale.x,0.0,
                0.0,_scale.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouse = ((iMouse.xy / iResolution.xy) - 0.5) * 1.5;

    vec2 uv_parallax = vec2(uv.x - mouse.x * 0.75, uv.y) * scale(vec2(1.3)) - vec2(.1);

    vec3 background = vec3(1.0, 1.0, 1.0);

    vec4 tex1 = texture(iChannel0, uv_parallax);
    vec4 tex2 = texture(iChannel1, vec2(uv_parallax.x - .75, uv_parallax.y));
    vec4 tex3 = texture(iChannel2, vec2(uv_parallax.x + .75, uv_parallax.y));



    vec3 shape1 = Box(uv, vec2(mouse.x, 0.0) , .3);
    shape1 *= 1.0 - tex1.rgb;

    vec3 shape2 = Box(uv, vec2(mouse.x + .75, 0.0) , .3);
    shape2 *= 1.0 - tex2.rgb;

    vec3 shape3 = Box(uv, vec2(mouse.x - .75, 0.0) , .3);
    shape3 *= 1.0 - tex3.rgb;

    vec3 mask = vec3(0.0, 0.0, 0.0);
    mask -= shape1;
    mask -= shape2;
    mask -= shape3;


    vec3 final = background + mask;

    fragColor = vec4(final,1.0);
}
