/* original  https://www.shadertoy.com/view/DlycWR https://www.shadertoy.com/view/clVyR1*/

#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
#define N  120
#define PI 3.141593

float circle(vec2 p, float r) {
    return smoothstep(.1, .0, abs(length(p) - r));
}

vec2 rotate(vec2 v, float angle) {
    float s = sin(angle);
    float c = cos(angle);
    return vec2(v.x * c - v.y * s, v.x * s + v.y * c);
}

void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,q,r=iResolution;
    vec2 uv =C/iResolution.xy;
    vec2 p2 = uv * 2. - 1.;
    p2.x *= iResolution.x / iResolution.y;
    p2 *= 3.;

    float a = atan(p2.y, p2.x);    

    vec3 col;


    for (int i = 0; i < N; i++) {
        float fi = float(i);
        float t = fi / float(N);
        float aa = (t + iTime / 12.) * 2. * PI;

        float size = .3 + sin(t * 6.* PI) * .1;


        float a1 = -iTime * PI / 3. + aa;       
        a1 += sin(length(p) * 3. + iTime * PI / 2.) * 0.3;
        vec2 c1 = vec2(cos(a1), sin(a1));

        float a2 = aa * 4.;            
        vec2 c2 = vec2(cos(a2), sin(a2)) * 0.3 + c1;
        col.r += .001 / abs(length(p2 - c2) - size);        
        col.g += .0013 / abs(length(p2 - c2) - size * 1.05);        
        col.b += .0015 / abs(length(p2 - c2) - size * 1.09);                
    }
  vec3  d=normalize(vec3((C*2.-r.xy)/r.y,1));  
    for(float i=0.,a,s,e,g=0.;
        ++i<110.;
        O.xyz+=mix(vec3(1),H(g*.1),sin(.8))*1./e/8e3

    )
    {
        p=g*d;
          p.xy=rotate(p.xy,iTime/10.-length(p.xy)*1.);
        p.z+=iTime*1.5;
        a=7.;
        p=mod(p-a,a*2.)-a;
        s=6.;
        for(int i=0;i++<8;){
            p=.3-abs(p);

            p.x<p.z?p=p.zyx:p;
            p.z<p.y?p=p.xzy:p;

            s*=e=1.4+sin(iTime*.234)*.1;
            p=abs(p)*e-
                vec3(
                    5.+sin(iTime*.3+.5*(iTime*.3))*3.,
                    120,
                    8.+cos(iTime*.2)*5.
                 )+col;
         }
         g+=e=length((p.yz)/s);
          g+=e=length((p.yx)/s);
    }
}
