void mainImage( out vec4 O,  vec2 U )
{
    U = U/iResolution.y;

    float w,a;
    U -= .5,
    w = length(U),                  // on Window, triggers the bug if "," instead of ";"
//  a = .001 / (w*w*w) ;            // no bug
//  a = .001 / pow(w,3.) ;          // bugged on Windows, but differently !!!
    a = .001 / pow(length(U),3.) ;  // bugged on Windows !!!

    O = vec4(a);
}
