void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float timeNormal = fract(iTime*0.2);
    float progress = clamp(smoothstep(uv.x-0.02,uv.x+0.02,timeNormal+0.02) - smoothstep(uv.x-0.02,uv.x+0.02,timeNormal),0.5,1.) ;
    vec4 color2 = vec4(0.,0.,progress ,1.);

    // Output to screen
    fragColor = color2;
}
