uvec3 k = uvec3(0x456789abu, 0x6789ab45u, 0x89ab4567u);
uvec3 u = uvec3(1, 2, 3);
const uint MAX = 0xffffffffu;

uvec3 uh(uvec3 n){
    n ^= (n.yzx << u);
    n ^= (n.yzx >> u);
    n *= k;
    n ^= (n.yzx << u);
    return n * k;
}

vec3 hash(vec3 p){
    uvec3 n = floatBitsToUint(p);
    return vec3(uh(n)) / vec3(MAX);
}

