vec4 dist(vec3 p){
    vec3 n = floor(p) + 0.5;
    vec4 d4 = vec4(length(1.5 - abs(p - n)));
    for(float k = 0.0; k <= 4.0; k ++ ){
        vec3 glid; 
        glid.z = n.z + sign(mod(k, 2.0) - 0.5) * ceil(k * 0.5);
        if (abs(glid.z - p.z) - 0.5 > d4.w){
            continue;
        }
        for(float j = 0.0; j <= 4.0; j ++ ){
            glid.y = n.y + sign(mod(j, 2.0) - 0.5) * ceil(j * 0.5);
            if (abs(glid.y - p.y) - 0.5 > d4.w){
                continue;
            }
            for(float i = -2.0; i <= 2.0; i ++ ){
                glid.x = n.x + i;
                vec3 jitter = hash(glid) - 0.5;
                d4 = s(d4, length(glid + jitter - p));
            }
        }
    }
    return d4;
}

vec4 wt; 
float noise(vec3 p){ 
    return abs(dot(wt*1.0, dist(p)));
}

