float Hash21(vec2 p){
    p = fract(p*vec2(234.34,435.345));
    p += dot(p, p+34.23);
    return fract(p.x*p.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;//change starting point to center

    uv *= iTime;
    vec3 col = vec3(0);
    vec2 gv = fract(uv)-.5;//grid uv; fract(x)= x - floor(x);-.5 move to center
    vec2 id = floor(uv);
    //col.gb = gv;//gradient

    float n = Hash21(id);//random number between 0 and 1

    //float mask = smoothstep(-.001, .001, gv.x +gv.y);//diagonal gradient smoothstep(min,max,x)blur/sharp boundary
    //float mask = abs(gv.x +gv.y);
    float width = .1;
    if(n<.5) gv.x*= -1.;//reverse direction
    float mask = smoothstep(.01, -.01, abs(gv.x +gv.y)-width);//-width,thickness

   //col.gb += mask;
   col.gb += n;//random gray

    if(gv.x>.3||gv.y>.45) col = vec3(.5,.5,1);//draw grid
    fragColor = vec4(col,1.0);

}
