precision highp int;
precision highp float;

float colorAt(vec2 t) {
    int x = int(floor(t.x));
    int y = int(ceil(t.x));
    // we can swap those, still fails
    //int x = int(floor(t.x));
    //int y = int(ceil(t.x));

    ivec2 xy = ivec2(x,y);
    if (t.y < .5){
        return x==xy.x? 0.5: 1.;
    } else {
        // This is true on my samsung galaxy s21???
        return y==xy.y? 0.5: 1.;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    fragColor = vec4(vec3(colorAt(uv)), 1.0);
}
