const vec2 speed = vec2(0.65, 1.05);
const float animCosPercent = 0.85;
const vec2 offset = vec2(2.8, 1.8);

const int maxIterations = 32;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 time = iTime * speed;
	vec2 scale = iResolution.xy / (offset * 2.0);

	vec2 cet = cos(time) * animCosPercent;
	vec2 pos = fragCoord / scale - offset;
	vec2 quad = pow(pos, vec2(2.0));

	int i;
	for (i = 0; i < maxIterations; i++)
	{
		pos.y = 2.0 * pos.x * pos.y + cet.y;
		pos.x = quad.x - quad.y + cet.x;
		quad.x = pow(pos.x, 2.0);
		quad.y = pow(pos.y, 2.0);
		if ((quad.x + quad.y) > 4.0)
			break;
	}
	float depth = float(i) / float(maxIterations);

	fragColor = vec4(vec3(depth), 1.0);
}
