const float TWO_PI = 6.28318530718;

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec2 proj(vec2 a) {
    vec2 a2 = a*a;
    vec2 s = vec2(1.,-1.);
    vec2 t = vec2(2.);
    vec2 trt = t*sqrt(2.);
    s = t + s * (a2.xx - a2.yy);
    return .5 * (sqrt(s + trt * a) - sqrt(s - trt * a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 pos = fragCoord / iResolution.yy - vec2(.5);

    if (length(pos) > .5) fragColor = vec4(0.);
    else if (length(pos) > .4) {
        fragColor = vec4(hsv2rgb(vec3(atan(pos.y,pos.x)/TWO_PI, 1., 1.)), 1.);
    }
    else if (length(pos) < .35) {
        fragColor = vec4(hsv2rgb(vec3(iTime / 10., (proj(pos / .35) + 1.) / 2.)), 1.);
    }
    else fragColor = vec4(0.);
}
