#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
#define TAU 6.283185
#define PI 3.141592
#define S smoothstep
#define T iTime * .4

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

vec2 N(float angle) {
// angle to vector
    return vec2(sin(angle), cos(angle));
}

vec3 Bg(vec3 rd) {
    float L = rd.y * .5 + .65;
    return vec3(L * L);
}

vec2 Koch(vec2 uv) {
    uv.x = abs(uv.x);

    vec3 col = vec3(0);
    float d;

    float angle = 0.;
    vec2 n = N((5./6.)*3.1415);

    uv.y += tan((5./6.)*3.1415)*.5;
   	d = dot(uv-vec2(.5, 0), n);
    uv -= max(0.,d)*n*2.;

    float scale = 1.;

    n = N((2./3.)*3.1415);
    uv.x += .5;

    int iters = 3;

    for(int i=0; i<iters; i++) {
        uv *= 3.;
        scale *= 3.;
        uv.x -= 1.5;

        uv.x = abs(uv.x);
        uv.x -= .5;
        d = dot(uv, n);
        uv -= min(0.,d)*n*2.;
    }
    uv /= scale;
    return uv;
}


float GetDist(vec3 p) {


    float size = 1.;
    float halfSize = size / 2.;

    p.xy -= halfSize;
    p.xy = mod(p.xy, size) - halfSize;

    vec2 uv = Koch(p.xy);


//    float x = (0.1 + cos(T) * 0.3) * (sin(p.z) *.5 + .5 + sin(T));

    float z = p.z - T;
    float x = (0.1) * (sin(z) *.5 + .5) ;
    float y = (0.1) * (sin(z * 2.324) * 2.9);

    float d = length(uv - vec2(x, y) - .1) - 0.04;;// innter cylinder

    x = sin(z*.3) * .4;
    y = .0;
    float o = length(uv - .3 - vec2(x, y) - sin(z) * .1) - 0.2 * sin(z) ;

    d = min(d, o);

    x = sin(z*.3) * .9;
    y = .10;
    float o2 = length(uv - 2.8 - vec2(x, y) - cos(z + .5) * .1) - 0.09 ;
    d = min(d, o2);


//    d = max(d, p.z);

    return d;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 3, -3);
    ro.yz *= Rot(-m.y*PI+1.);
    ro.xz *= Rot(-m.x*TAU);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0),1.);
    vec3 bg = Bg(rd);
    vec3 col = bg;

    float d = RayMarch(ro, rd);

    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        float dif = n.y*.5 +.5 + sin(T) * .25;
        col = vec3(dif);

        col = mix(col, bg, S(1., 10., d));
        col.r -= 3. * n.z * sin(T);
        col.b += cos(col.r) * n.z;
    }

//    col.g = st.x;

    /*col *= 0.;
    vec2 st = Koch(uv * 1.5)*4.;
    //col = vec3(st.y);    
    col.g = st.x;

    col += S(.01, .0, length(st - vec2(.0, -0.6)) - .1);
    col = pow(col, vec3(.4545));
    */
    fragColor = vec4(col,1.0); 
}
