void mainImage (out vec4 o, vec2 u)
{
    u /= iResolution.x;
    float t = iTime, c = fract (.04*(t+u.x));
    vec4 x; o *= x;
    for ( ++o.y; o.a++<99.; )
        o += vec4 (1, u, 0) * (length (cos (o.xyz + t)) - .3);
    u -= .5;
    for (ivec4 b; (b.x^b.y^b.z)%99 > b.z-7; )
        b = ivec4 ( t*vec2(2,4) + 6.*u*x.a, x+=.1 );
    o = c < .3 ? o/50.
      : c < .6 ? max ( u = fract (u/dot (u,u)-t), u.x - u ).yyyy 
      : x/74.;
}
