void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float c = cos(iTime);
    float s = sin(iTime);
    float c2 = cos(iTime/2.0);
    float s2 = sin(iTime/2.0);

    vec2 uv[4];
    uv[0] = (fragCoord/iResolution.xy * 2.0 - 1.0 - 0.25) * (1.5+s/2.0 );

    mat2 rot = mat2(c2, -s2, s2,c2);
    uv[0] = uv[0] * rot;


    uv[1] = uv[0] * uv[0];
    uv[2] = uv[1] * uv[0];
    uv[3] = uv[2] * uv[0];


    float cx = 1.2+c/5.0;
    float sx = 1.2+s/5.0;

    float rx[4];
    rx[0]=1.0;rx[1]=1.0;rx[2]=1.0;rx[3]=cx;
    float gx[4];
    gx[0]=1.0;gx[1]=1.0;gx[2]=sx;gx[3]=1.0;
    float bx[4];
    bx[0]=1.0;bx[1]=cx*sx;bx[2]=1.0;bx[3]=1.0;

    float ry[4];
    ry[0]=1.0;ry[1]=1.0;ry[2]=cx*sx;ry[3]=1.1;
    float gy[4];
    gy[0]=1.0;gy[1]=1.0;gy[2]=1.1;gy[3]=cx;
    float by[4];
    by[0]=1.0;by[1]=1.1;by[2]=sx;by[3]=1.0;

    vec3 col = vec3(0.0);
    for(int i=0;i<4;++i) {
        col.r+=rx[i]*uv[i].x+ry[i]*uv[i].y;
        col.g+=gx[i]*uv[i].x+gy[i]*uv[i].y;
        col.b+=bx[i]*uv[i].x+by[i]*uv[i].y;
    }
    col=abs(col);
    col.r=pow(col.r,0.3);
    col.g=pow(col.g,0.3);
    col.b=pow(col.b,0.3);
    col=fract(col);

    fragColor = vec4(col,1.0);
}
