void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv=fragCoord/iResolution.xy*2.0-1.0;
    uv.x*=iResolution.x/iResolution.y;
    float d=length(uv);
    vec3 color=vec3(1.0,2.0,3.0);
    d=sin(d*4.0-iTime)/4.;
    d=abs(d);
    d=0.005/d;
    color*=d;
    fragColor = vec4(color,1.0);
}
