vec2 multi(vec2 a, vec2 b) {
    return vec2(a.x*b.x-a.y*b.y, a.x*b.y+a.y*b.x);
}

float sqdst(vec2 a) {
    return (a.x*a.x+a.y*a.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouseUv = iMouse.xy/iResolution.xy;
    // (0, 0) to (3, 2)
    uv = vec2(uv.x*2.0, uv.y*3.0);
    // (-2, -1) to (1, 1)
    uv = uv-vec2(1.0,1.5);
    // Time varying pixel color

    mouseUv = vec2(mouseUv.x*2.0, mouseUv.y*2.0);
    mouseUv -= vec2(1.0);

    float col = 0.0;
    vec2 ps = uv;
    for (int i = 0; i < 500; i++) {
        ps = multi(ps, ps);
        ps = ps+(mouseUv);
        col += 1.0;
        if (sqdst(ps) > 4.0) {
            break;
        }
    }
    if (col > 499.5) {
        fragColor = vec4(0.0);
    } else {
        col /= 30.0;
        col += 0.2;
        fragColor = vec4(1.0, col, 1.0, 1.0);
    }
    // Output to screen
}
