vec3 rainbowColor[6] = vec3[6] (
    vec3(1.0, 0.0, 0.0), // Red
    vec3(1.0, 0.6, 0.0), // Orange
    vec3(1.0, 1.0, 0.0), // Yellow
    vec3(0.2, 1.0, 0.0), // Green
    vec3(0.0, 0.6, 1.0), // Blue
    vec3(0.4, 0.2, 1.0) // Violet
);

vec3 bgColor = vec3(0.0, 0.2, 0.4);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;

    uv *= 2.;
    uv -= vec2(2.5, 0.5);
    uv = clamp(uv, 0., 1.);

	float frame = floor( mod( iTime*10.0, 6.0 ) );

    //vec2 offset = vec2(0.2);
    //float scale = 3.;
    vec2 rainbowUv = uv;
    rainbowUv.x = fragCoord.x / iResolution.x;

    float s = sin((rainbowUv.x - 0.5f) * 15.0 + iTime * 5.0f) * 0.1;
	s -= mod(s, 0.05);
	rainbowUv.y+=s;

	float nyanWidthUV = 40.0/256.0;
	uv.x = (uv.x + frame)*nyanWidthUV;

    vec3 bg = bgColor;

    // rainbow part
    if (rainbowUv.x < 0.78f && abs(rainbowUv.y - 0.5) < 0.2) {
        float i = (rainbowUv.y - 0.3) / 0.4f;
        for (int j = 0; j < 6; ++j) {
            if (i * 6.0f < float(j + 1)) {
                bg = rainbowColor[5-j];
                break;
            }
        }

    }

	vec4 nyan = texture( iChannel0, uv );
	vec3 col = nyan.xyz;
    col = mix(bg, col, nyan.a);



    fragColor = vec4(col, 1.0 );
}
