void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;

    uv.x *= iResolution.x/iResolution.y;


    // Time varying pixel color
    float d = length(uv);
    float radius = 0.3;

    float customTan  = tan(iTime);
    customTan =  2.3 + 5.*smoothstep(0.01,.2,cos(uv.x/uv.y* iResolution.x/iResolution.y +iTime) * uv.x) * uv.y;

    float lightingSin = tan(iTime);
    lightingSin = (lightingSin + 0.0)/.8;
    float c = smoothstep(radius, radius - .0, d);
    c = pow(0.087 / d * customTan, 1.2);

    vec3 color = vec3(c - .6,c - 0.05,c - 0.1);

    // Output to screen
    fragColor = vec4(color,1.0);
}
