void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv.y = 1.5 - uv.y; // Invert Y-coordinate to match typical image coordinates.

    // Calculate continuous rotation (like a spinning record)
    float rotationSpeed =0.1; // Adjust the rotation speed as needed
    float continuousRotation = mod(iTime * rotationSpeed, 0.5 * 3.14159);

    // Calculate tumbling or spinning effect
    float tumbleSpeed = 0.1; // Adjust the tumble speed as needed
    float tumbleAngle = iTime * tumbleSpeed;

    // Combine the continuous rotation and tumble effect
    float finalRotation = continuousRotation + tumbleAngle;

    // Apply combined rotation to the UV coordinates
    vec2 rotatedUV = vec2(cos(finalRotation) * (uv.x - 0.5) - sin(finalRotation) * (uv.y - 0.5) + 0.5, 
                          sin(finalRotation) * (uv.x - 0.5) + cos(finalRotation) * (uv.y - 0.2) + 0.2);

    // Sample the texture
    vec3 logoColor = texture(iChannel0, rotatedUV).rgb;

    // Set the background to transparent
    fragColor = vec4(logoColor, 5.0);
}
