vec2 jumpUsingIntensityAsAngle(vec2 uv,float radius){
  vec3 color = texture(iChannel0,uv).xyz;
  float intensity = dot(color,vec3(1./3.f));
  float angle = radians(intensity*360.f);
  return uv+vec2(cos(angle),sin(angle))*radius;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;


    if(fragCoord.x > iMouse.x)
        for(int i=0;i<10;++i)
            uv = jumpUsingIntensityAsAngle(uv,0.002f);



    fragColor = texture(iChannel0,uv);

}
