void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float noteTime = fract(iTime);
    float amplitude = sqrt(noteTime)*(1.-noteTime)*2.5;

    // Output to screen
    fragColor = vec4(col*amplitude,1.0);
}

#define TAU 6.28318531
const float baseNote = 30.87;
const float stride = pow(2.,1./12.);
const float maxNote = 60.;

vec2 mainSound( int samp, float time )
{
    float index = mod(floor(time),maxNote);
    float note = baseNote * pow(stride, index);
    float amplitude = sqrt(fract(time))*(1.-fract(time))*1.5;
    return vec2( sin(TAU*note*time) * amplitude);
}
