void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // uv coordinate normalization
    vec2 uv = fragCoord/iResolution.xy;
    // k of liner function (always=1 after uv)
    float k = 1.0;

    // default color
    fragColor = vec4(1.0);    
    // triangle color    
    if (uv.y/uv.x < k) {
        vec3 triColor = vec3(uv.x,uv.y,cos(uv.xy));    
        fragColor = vec4(triColor ,1.0);
    }
}
