vec3 mask(bool b, vec2 uv, float v){
    float y;
    //  1/partition / position
    // y = step(uv.x, 1./3.*3.);
    if (b) {
        y = step(v, uv.x);
    } else {
        y = step(uv.x, v);
    }

    vec3 color = vec3(y);
    float pct = plot(uv,0.);
    color = (0.999-pct)*color;

    return color;
}


