void mainImage(out vec4 o, vec2 i) {
    float v = dot(
        vec4(1.6, 5.6, .8, 0), 
        sqrt(texture(iChannel0, i / iResolution.xy))
    );

    o = vec4(vec4(0,4,2,6)[int(i.x)%4] < v 
          && vec4(1,5,3,7)[int(i.y)%4] < v);
}


