//#define TEST_GRADIENT
//#define PALETTE_GRAY

const vec2 PIXEL_SIZE = vec2(256.0, 144.0);

vec3 Palette(float v) {
    #ifdef PALETTE_GRAY
        return vec3(v / 3.0);
    #endif

    if (v < 1.0) return vec3(0.0, 0.25, 0.0);
    if (v < 2.0) return vec3(0.18, 0.451, 0.125);
    if (v < 3.0) return vec3(0.55, 0.75, 0.04);
    return vec3(0.625, 0.812, 0.04);
}

float RGB2Gray(vec3 color) {
    return 0.299 * color.r + 0.587 * color.g + 0.114 * color.b;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Remap UVs
    vec2 uv = floor(fragCoord / iResolution.xy * PIXEL_SIZE);
    vec2 id = uv / iChannelResolution[0].xy;
    uv /= PIXEL_SIZE;

    // Get textures
    vec3 tex = texture(iChannel1, uv).rgb;
    float bayer = texture(iChannel0, id).r - 0.5;

    // Convert to grayscale and apply dithering
    float v = RGB2Gray(tex);
    #ifdef TEST_GRADIENT
        v = uv.y;
    #endif
    float smoothness = 0.5;
    if (iMouse.z > 0.0) { smoothness = iMouse.x / iResolution.x; }
    v = round(v * 3.0 + bayer * smoothness);

    // Apply palette
    vec3 color = Palette(v);

    fragColor = vec4(color, 1.0);
}
